require 'helper'


class TestCalldata < Minitest::Test


 CALLDATA = [
    ["data:image/png;base64,...", 
     "646174613a696d6167652f706e673b6261736536342c2e2e2e"],

    ["Hello, World!",
    "48656c6c6f2c20576f726c6421"],

    # tx 0xb1bdb91f010c154dd04e5c11a6298e91472c27a347b770684981873a6408c11c
    # inscribe no. 0 (genesis!)
    [
      "data:image/jpeg;base64,",
      ""
    ],
 ]
 
def test_calldata
   CALLDATA.each_with_index do |(utf8,hex), i|
     puts "==> calldata test case no. #{i+1}"

     assert_equal utf8, hex_to_utf8( hex )
     assert_equal utf8, Calldata.decode( hex ) # hex to utf8

     assert_equal hex,  utf8_to_hex( utf8 )
     assert_equal hex,  Calldata.encode( utf8 )  # utf8 to hex

     assert_equal hex, utf8_to_hex(hex_to_utf8( hex ))
     assert_equal hex, Calldata.encode(Calldata.decode( hex ))
     assert_equal hex, Calldata.encode_utf8(Calldata.decode_hex( hex ))
     assert   Calldata.valid?( hex )
   end
end



def test_zero
   ## test single digit bytes e.g. 0,3  
   ## note: 0 byte gets removed!!!

   pp utf8 = hex_to_utf8( "0003" )
   assert_equal "\x03", utf8
   assert_equal  Encoding::UTF_8, utf8.encoding
   
   pp hex = utf8_to_hex( "\x00\x03" )  ## (auto)remove here too - why? why not?
   assert_equal "0003", hex
   assert_equal  Encoding::US_ASCII, hex.encoding
end

