###
#  to run use
#     ruby -I ./lib -I ./test test/test_datauri.rb


require 'helper'


class TestDataUri < Minitest::Test


def test_empty
   uri = "data:,"

   assert                 DataUri.valid?( uri )  
   assert_equal ['', ''], DataUri.parse( uri )
end    


def test_images
    ################
    ## scribe  no. 17
    uri = 'data:image/png;base64,'

    assert DataUri.valid?( uri )  
   
    mediatype, data = DataUri.parse( uri )
    assert_equal 'image/png', mediatype

    pp data.encoding   ## check if set to binary by base64 decode?
    ## yes, it's #<Encoding:ASCII-8BIT>
 
    write_blob( "./tmp/17.png", data )

    ##########
    ## scribe no. 15
    uri = 'data:image/png;base64,'

    assert DataUri.valid?( uri )  
   
    mediatype, data = DataUri.parse( uri )
    assert_equal 'image/png', mediatype

    pp data.encoding   ## check if set to binary by base64 decode?
    ## yes, it's #<Encoding:ASCII-8BIT>
 
    write_blob( "./tmp/15.png", data )

    ######
    ## scribe no. 0
    uri = 'data:image/jpeg;base64,'

    assert DataUri.valid?( uri )  
   
    mediatype, data = DataUri.parse( uri )
    assert_equal 'image/jpeg', mediatype

    pp data.encoding   ## check if set to binary by base64 decode?
    ## yes, it's #<Encoding:ASCII-8BIT>
    write_blob( "./tmp/0.jpeg", data )
end


end   # class TestDataUri

